package javaprogramming;

public class poly {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		poly obj=new poly();
		obj.add(10,20);
		
	}
	void add(int x,int y)
	{
		int sum=x+y;
		System.out.println(sum);
		
	}
	void add(double x,double y)
	{
		double sum=x+y;
		System.out.println(sum);
		
	}
	void add(int x,int y,int z)
	{
		int sum=x+y+z;
		System.out.println(sum);
		
	}



}
